actions.on:
print('ON command received');

// Switch on all ECHONET Lite devices
callProc({method:'PUT',path:'/v1/echonet/.+/OperatingState',args:{value:'on'}})
 .then(resolve)
 .catch(reject);

actions.off:
print('OFF command received');

// Switch off all ECHONET Lite devices
callProc({method:'PUT',path:'/v1/echonet/.+/OperatingState',args:{value:'off'}})
 .then(resolve)
 .catch(reject);
